    layer_name = f'conv{i+1}'
    if i == 0:
        layer = conv_block(c, fea_channel, kernel_size=1, relu=False)
    else:
        layer = nn.Sequential(
            conv_block(c, fea_channel, kernel_size=1, relu=False),
            nn.Upsample(scale_factor=2**i, mode='nearest'),
        )
    self.add_module(layer_name, layer)

