BEFORE:
for layer in self._order:
    if layer == 'msg_pass':
        x = self.msg_pass(x, graph)
    elif layer == 'norm' and self._with_norm:
        x = self.norm(x)
    elif layer == 'act' and self._with_act:
        x = self.act(x)

AFTER:
for layer in self._order:
    if layer == 'msg_pass':
        x = self.msg_pass(x, graph)
    else:
        x = getattr(self, layer)(x)
