def __init__(self, dim, mult = 4, glu = False):
    super().__init__()
    project_in = nn.Sequential(
        nn.Linear(dim, dim * mult),
        nn.GELU()
    ) if not glu else GEGLU(dim, dim * mult)

    self.net = nn.Sequential(
        project_in,
        nn.Linear(dim * mult, dim)
    )
